USMAP <- plotTable %>% 
  plot_ly(
    type = 'choropleth',
    locations= ~statesOver,
    locationmode = 'USA-states' , 
    colorscale='tempo', z=~logSold,
    zmin=0, zmax=6,
    text=~cumulative,
    hoverinfo='text',
    showscale=FALSE,
    frame=~day) %>% 
  layout(geo=list( scope = 'usa' )) 
library(htmlwidgets)
saveWidget(USMAP, "USMAP.html")












